mapper.select:
    SELECT *
    FROM ${type}
    WHERE
	${colum1} LIKE #{data1} AND ${colum2} LIKE #{data2}

select count(*) 
from comments c,board b  
where c.board_id like #{x} 
	and b.board_id like #{x}

mapper.update:
    UPDATE BOARD
  			SET view_num = IFNULL(view_num, 0) + 1
			WHERE BOARD_ID = #{x}

UPDATE board
SET contents = #{contents},
	co_title = #{title}
WHERE board_id = #{x}

mapper.select:
select count(*) from book b join genre g
 on g.genreNum LIKE b.genreNum
  where g.largeGenre like #{genre}
